mapper.select:
select
	pu.id, pu.is_leader, pu.project_id, pu.user_id, pu.type, pu.service_type_id, pu.createtime,
	pu.is_del
from project_user pu inner join project p on pu.project_id = p.id
where 
pu.is_del=0

	AND pu.project_id=#{projectId}


	AND pu.user_id=#{userId}


	AND pu.type = #{type}


	and pu.is_leader=#{isLeader}

limit 0,1

select
	pu.id, pu.is_leader, pu.project_id, pu.user_id, pu.type, pu.service_type_id, pu.createtime,
	pu.is_del
from project_user pu inner join project p on pu.project_id = p.id
where 
pu.is_del=0

	AND pu.project_id=#{projectId}


	AND pu.user_id=#{userId}


	AND pu.type = #{type}


	AND pu.service_type_id =#{serviceTypeId}


	AND pu.is_leader=#{isLeader}

limit 0,1

select
	pu.id, pu.is_leader, pu.project_id, pu.user_id, pu.type, pu.service_type_id, pu.createtime,
	pu.is_del,u.name user_name,u.head_pic userHeadPic
from project_user pu inner join project p on pu.project_id = p.id
inner join user u on pu.user_id = u.id
where 
pu.is_del=0

	AND pu.project_id=#{projectId}


	AND pu.user_id=#{userId}


	AND pu.type = #{type}


	AND pu.service_type_id =#{serviceTypeId}


	AND pu.is_leader=#{isLeader}

ORDER BY pu.is_leader desc

SELECT res.* from( select pu.id, CASE pu.user_id WHEN #{userId} THEN pu.is_leader ELSE 0 END is_leader, pu.project_id, pu.user_id, pu.type, pu.service_type_id, pu.createtime, pu.is_del,u.name user_name,u.head_pic userHeadPic from project_user pu inner join project p on pu.project_id = p.id inner join user u on pu.user_id = u.id where pu.is_del=0 AND pu.project_id=#{projectId} AND pu.type = #{type}
AND pu.user_id IN (SELECT id FROM `user` WHERE company_id = #{companyId})
AND pu.user_id=#{userId}
ORDER BY pu.is_leader desc ) res GROUP BY res.service_type_id
